SELECT ut.user_id, ut.username, ut.password, ut.email, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id, urt.create_dt AS urt_create_dt, urt.update_dt AS urt_update_dt,
rt.role_id, rt.role_name, rt.role_name_kor FROM user_tb ut LEFT JOIN user_role_tb urt ON (ut.user_id = urt.user_id) LEFT JOIN role_tb rt ON (urt.role_id = rt.role_id) WHERE ut.user_id = #{userId}

SELECT ut.user_id, ut.username, ut.password, ut.email, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id, urt.create_dt AS urt_create_dt, urt.update_dt AS urt_update_dt,
rt.role_id, rt.role_name, rt.role_name_kor FROM user_tb ut LEFT JOIN user_role_tb urt ON (ut.user_id = urt.user_id) LEFT JOIN role_tb rt ON (urt.role_id = rt.role_id) WHERE ut.username = #{username}

SELECT ut.user_id, ut.username, ut.password, ut.email, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id, urt.create_dt AS urt_create_dt, urt.update_dt AS urt_update_dt,
rt.role_id, rt.role_name, rt.role_name_kor FROM user_tb ut LEFT JOIN user_role_tb urt ON (ut.user_id = urt.user_id) LEFT JOIN role_tb rt ON (urt.role_id = rt.role_id) WHERE ut.email = #{email}
